
public class PrintFormatTest_01 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.printf("왼쪽정렬:%-10d\n", 20);
		System.out.printf("오른쪽정렬:%10d\n", 20);
		System.out.printf("10진 정수형:%d\n", 10);
		System.out.printf("8진 정수형:%o\n", 10);
		System.out.printf("소수점:%3.2f\n", 88.7777);
	}

}
