
public class InDecoperTest_01 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int x = 100;
		System.out.println("전위 증가 연산자 (++x)");
		System.out.printf("x의 현재값 = %d\n", (++x));
		System.out.println();
		
		x=200;
		System.out.println("후위 증가 연산자 (x++)");
		System.out.printf("x의 현재값 = %d\n",(x++));
	}

}
