
public class CompAssignTest01 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int x=100;
		
		x+=10;   //x=x+10;
		System.out.printf("x += %d\n", x);
		
		x=100;
		x-=10;  //x=x-10;
		System.out.printf("x -= %d\n", x);
		
		x=100;
		x*=10;  //x=x*10;
		System.out.printf("x *= %d\n", x);
		
		x=100;
		x/=10;  //x=x%10;
		System.out.printf("x /= %d\n", x);
	}

}
