
public class TypeConveTest02 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		char c='A';
		int i=c;
		System.out.printf("c=%d\n",i);
		
		int x=(int)4.52;
		System.out.printf("x=%d\n", x);
		
		double d=12345;
		System.out.printf("d=%f\n", d);
		
		double avg=96.78;
		int sel=(int)avg/10;
		System.out.printf("sel=%d\n", sel);

	}

}
